import Cryptofrom Crypto.PublicKey import RSAfrom Crypto import Randomimport db#Generte new keypair and save it in 2 seperate filesdef newKey():	random_generator = Random.new().read	key = RSA.generate(1024, random_generator)		privKey = key.exportKey()	pubKey =  key.publickey().exportKey()		f = open ('privateKey.txt', 'w')	f.write(privKey)	f.close()		f = open ('publicKey.txt', 'w')	f.write(pubKey)	f.close()